type arrayElement=string| number | arrayElement[]


export const transformValue = (val: unknown): unknown => {
    const clonedVal:Object ={...val as Object}
    const solution:Record<string,unknown>={}
    for(const [key,value] of Object.entries(clonedVal)){
        solution[key]=doTransformation(value)

    }



console.log(solution)
    return solution;
}
const doTransformation=(arrayElement:arrayElement)=>{
    if (typeof arrayElement ==='number')
    return arrayElement+1;
    else if(typeof arrayElement==='string')
    return arrayElement+ ' AE'
    else 
    {
        const newArray=arrayElement.map(el=>doTransformation)
        return newArray
    }
}
